import{_ as t,d as e}from"./app.7e3542fd.js";const r={},d=e('<h1 id="\u672F\u8BED\u8868" tabindex="-1"><a class="header-anchor" href="#\u672F\u8BED\u8868" aria-hidden="true">#</a> \u672F\u8BED\u8868</h1><h2 id="web" tabindex="-1"><a class="header-anchor" href="#web" aria-hidden="true">#</a> Web</h2><p>Web \u5F00\u53D1\u6DB5\u76D6\u4E86\u8BB8\u591A\u4E13\u4E1A\u672F\u8BED\u3002\u672C\u7BC7\u65E8\u5728\u6536\u96C6\u5E76\u89E3\u91CA\u8FD9\u4E9B\u672F\u8BED\uFF0C\u65B9\u4FBF\u884C\u5185\u4EBA\u5458\u53CA\u65B0\u624B\u67E5\u8BE2\u4F7F\u7528\u3002</p><h3 id="spa-\u5355\u9875\u5E94\u7528" tabindex="-1"><a class="header-anchor" href="#spa-\u5355\u9875\u5E94\u7528" aria-hidden="true">#</a> SPA (\u5355\u9875\u5E94\u7528)</h3><p><strong>\u5355\u9875\u5E94\u7528\uFF08Single-Page Application, SPA\uFF09</strong> \u662F\u4E00\u79CDWeb\u5E94\u7528\u7A0B\u5E8F\u7684\u67B6\u6784\u6A21\u578B\u3002\u5728SPA\u4E2D\uFF0C\u9875\u9762\u8DF3\u8F6C\u901A\u8FC7JavaScript\u52A8\u6001\u5730\u91CD\u5199\u5F53\u524D\u9875\u9762\u800C\u975E\u4ECE\u670D\u52A1\u5668\u52A0\u8F7D\u6574\u4E2A\u65B0\u9875\u9762\u5B9E\u73B0\uFF0C\u4ECE\u800C\u63D0\u9AD8\u7528\u6237\u4F53\u9A8C\u3002</p><ul><li>\u4F18\u52BF: \u63D0\u5347\u54CD\u5E94\u901F\u5EA6\uFF0C\u7528\u6237\u4F53\u9A8C\u66F4\u6D41\u7545\uFF0C\u9875\u9762\u5207\u6362\u7C7B\u4F3C\u684C\u9762\u5E94\u7528\u7A0B\u5E8F\u3002</li><li>\u7F3A\u70B9: \u521D\u59CB\u52A0\u8F7D\u8F83\u6162\uFF0C\u9996\u6B21\u52A0\u8F7D\u5168\u90E8\u8D44\u6E90\u53EF\u80FD\u5BFC\u81F4\u767D\u5C4F\u95EE\u9898\uFF1B\u5355\u9875\u7ED3\u6784\u4E5F\u53EF\u80FD\u5BF9\u641C\u7D22\u5F15\u64CE\u4F18\u5316\uFF08SEO\uFF09\u4E0D\u5229\u3002</li></ul><h3 id="ssr-\u670D\u52A1\u7AEF\u6E32\u67D3" tabindex="-1"><a class="header-anchor" href="#ssr-\u670D\u52A1\u7AEF\u6E32\u67D3" aria-hidden="true">#</a> SSR (\u670D\u52A1\u7AEF\u6E32\u67D3)</h3><p><strong>\u670D\u52A1\u7AEF\u6E32\u67D3\uFF08Server-Side Rendering, SSR\uFF09</strong> \u6307\u7684\u662FWeb\u9875\u9762\u7684HTML\u76F4\u63A5\u7531\u670D\u52A1\u5668\u751F\u6210\uFF0C\u7136\u540E\u53D1\u9001\u5230\u5BA2\u6237\u7AEF\u7684\u6280\u672F\u3002\u8FD9\u79CD\u65B9\u6CD5\u53EF\u4EE5\u63D0\u5347\u9996\u5C4F\u52A0\u8F7D\u901F\u5EA6\uFF0C\u5E76\u5BF9\u641C\u7D22\u5F15\u64CE\u4F18\u5316\u66F4\u4E3A\u53CB\u597D\u3002</p><ul><li>\u4F18\u52BF: \u6539\u5584\u9996\u5C4F\u52A0\u8F7D\u65F6\u95F4\uFF0C\u63D0\u5347SEO\u8868\u73B0\u3002</li><li>\u7F3A\u70B9: \u670D\u52A1\u5668\u8D1F\u8F7D\u589E\u52A0\uFF0C\u53EF\u80FD\u5F71\u54CD\u540E\u7AEF\u6027\u80FD\u3002</li></ul><h3 id="devops-\u5F00\u53D1\u8FD0\u7EF4\u4E00\u4F53\u5316" tabindex="-1"><a class="header-anchor" href="#devops-\u5F00\u53D1\u8FD0\u7EF4\u4E00\u4F53\u5316" aria-hidden="true">#</a> DevOps\uFF08\u5F00\u53D1\u8FD0\u7EF4\u4E00\u4F53\u5316\uFF09</h3><p>DevOps\u662F\u4E00\u79CD\u6587\u5316\u548C\u5B9E\u8DF5\uFF0C\u65E8\u5728\u52A0\u901F\u5F00\u53D1\u4E0E\u8FD0\u7EF4\u56E2\u961F\u4E4B\u95F4\u7684\u534F\u4F5C\uFF0C\u901A\u8FC7\u81EA\u52A8\u5316\u8F6F\u4EF6\u4EA4\u4ED8\u8FC7\u7A0B\u6765\u63D0\u9AD8\u6548\u7387\u3002</p><ul><li><strong>\u6838\u5FC3\u5B9E\u8DF5</strong>: \u6301\u7EED\u96C6\u6210 (CI)\u3001\u6301\u7EED\u4EA4\u4ED8 (CD)\u3002</li><li><strong>\u5DE5\u5177\u793A\u4F8B</strong>: Jenkins, Docker, Kubernetes \u7528\u4E8E\u81EA\u52A8\u5316\u6D4B\u8BD5\u3001\u90E8\u7F72\u548C\u7BA1\u7406\u5E94\u7528\u7A0B\u5E8F\u7684\u5BB9\u5668\u5316\u3002</li></ul><h3 id="rwd-\u54CD\u5E94\u5F0F\u8BBE\u8BA1" tabindex="-1"><a class="header-anchor" href="#rwd-\u54CD\u5E94\u5F0F\u8BBE\u8BA1" aria-hidden="true">#</a> RWD (\u54CD\u5E94\u5F0F\u8BBE\u8BA1)</h3><p>\u54CD\u5E94\u5F0F\u7F51\u7AD9\u8BBE\u8BA1 (RWD) \u662F\u4E00\u79CD\u7F51\u9875\u8BBE\u8BA1\u65B9\u6CD5\u8BBA\uFF0C\u76EE\u7684\u662F\u6839\u636E\u7528\u6237\u7684\u8BBE\u5907\uFF08\u684C\u9762\u3001\u5E73\u677F\u3001\u624B\u673A\uFF09\u6765\u63D0\u4F9B\u9002\u5408\u9605\u8BFB\u548C\u5BFC\u822A\u7684\u89C6\u56FE\u3002</p><ul><li><strong>\u6280\u672F\u5B9E\u73B0</strong>: \u5A92\u4F53\u67E5\u8BE2, \u5F39\u6027\u5E03\u5C40\u3002</li><li><strong>\u5DE5\u5177\u6846\u67B6</strong>: Bootstrap, Foundation\u3002</li></ul><h3 id="serverless-\u65E0\u670D\u52A1\u5668\u67B6\u6784" tabindex="-1"><a class="header-anchor" href="#serverless-\u65E0\u670D\u52A1\u5668\u67B6\u6784" aria-hidden="true">#</a> Serverless (\u65E0\u670D\u52A1\u5668\u67B6\u6784)</h3><p>\u65E0\u670D\u52A1\u5668\u67B6\u6784\u662F\u4E00\u79CD\u6267\u884C\u4EE3\u7801\u7684\u65B9\u5F0F\uFF0C\u5F00\u53D1\u8005\u65E0\u9700\u660E\u786E\u7BA1\u7406\u670D\u52A1\u5668\uFF0C\u53EA\u9700\u7F16\u5199\u4E1A\u52A1\u903B\u8F91\u4EE3\u7801\u3002</p><ul><li><strong>\u4F18\u70B9</strong>: \u51CF\u5C11\u7BA1\u7406\u670D\u52A1\u5668\u7684\u9700\u6C42\uFF0C\u81EA\u52A8\u6269\u5C55\u548C\u7F29\u653E\u3002</li><li><strong>\u5E73\u53F0\u793A\u4F8B</strong>: AWS Lambda, Google Cloud Functions, Azure Functions\u3002</li></ul><h3 id="pwa-progressive-web-apps" tabindex="-1"><a class="header-anchor" href="#pwa-progressive-web-apps" aria-hidden="true">#</a> PWA (Progressive Web Apps)</h3><p>PWA \u662F\u4E00\u79CD\u521B\u5EFA\u7C7B\u4F3C\u539F\u751F\u5E94\u7528\u4F53\u9A8C\u7684Web\u5E94\u7528\u7684\u65B9\u6CD5\u3002\u5B83\u5141\u8BB8\u7528\u6237\u5728\u79BB\u7EBF\u65F6\u4F7F\u7528\uFF0C\u5E76\u53EF\u4EE5\u5B89\u88C5\u5230\u7528\u6237\u7684\u4E3B\u5C4F\u5E55\u4E0A\u3002</p><ul><li><strong>\u5173\u952E\u6280\u672F</strong>: Service Workers, Manifest\u6587\u4EF6, \u79BB\u7EBF\u5B58\u50A8\u3002</li><li><strong>\u4F18\u52BF</strong>: \u63D0\u5347\u6027\u80FD\uFF0C\u589E\u5F3A\u7528\u6237\u4F53\u9A8C\uFF0C\u79BB\u7EBF\u5DE5\u4F5C\u80FD\u529B\u3002</li></ul><hr><h2 id="\u5546\u4E1A\u6280\u672F\u672F\u8BED\u8868" tabindex="-1"><a class="header-anchor" href="#\u5546\u4E1A\u6280\u672F\u672F\u8BED\u8868" aria-hidden="true">#</a> \u5546\u4E1A\u6280\u672F\u672F\u8BED\u8868</h2><p>\u5E38\u89C1\u7B80\u5199\u4E00\u89C8\uFF1A</p><table><thead><tr><th>name</th><th>description</th></tr></thead><tbody><tr><td>toB</td><td>\u9762\u5411\u4F01\u4E1A\u7684\u4EA7\u54C1 (To Business)</td></tr><tr><td>toC</td><td>\u9762\u5411\u6D88\u8D39\u8005\u7684\u4EA7\u54C1 (To Customer)</td></tr><tr><td>OA</td><td>\u529E\u516C\u81EA\u52A8\u5316 (Office Automation)</td></tr><tr><td>ERP</td><td>\u4F01\u4E1A\u8D44\u6E90\u8BA1\u5212 (Enterprise Resource Planning)</td></tr><tr><td>CRM</td><td>\u5BA2\u6237\u5173\u7CFB\u7BA1\u7406 (Customer Relationship Management)</td></tr><tr><td>CMS</td><td>\u5185\u5BB9\u7BA1\u7406\u7CFB\u7EDF (Content Management System)</td></tr><tr><td>SaaS</td><td>\u8F6F\u4EF6\u5373\u670D\u52A1 (Software as a Service) - \u901A\u8FC7\u4E92\u8054\u7F51\u63D0\u4F9B\u8F6F\u4EF6\u5E94\u7528</td></tr><tr><td>PaaS</td><td>\u5E73\u53F0\u5373\u670D\u52A1 (Platform as a Service) - \u63D0\u4F9B\u4E92\u8054\u7F51\u5F00\u53D1\u5E73\u53F0</td></tr><tr><td>IaaS</td><td>\u57FA\u7840\u8BBE\u65BD\u5373\u670D\u52A1 (Infrastructure as a Service) - \u63D0\u4F9B\u865A\u62DF\u8BA1\u7B97\u8D44\u6E90</td></tr><tr><td>BI</td><td>\u5546\u4E1A\u667A\u80FD (Business Intelligence) - \u5206\u6790\u6570\u636E\u652F\u6301\u51B3\u7B56</td></tr><tr><td>IoT</td><td>\u7269\u8054\u7F51 (Internet of Things) - \u8FDE\u63A5\u8BBE\u5907\u7684\u7F51\u7EDC\u6536\u96C6\u4EA4\u6362\u6570\u636E</td></tr><tr><td>AI</td><td>\u4EBA\u5DE5\u667A\u80FD (Artificial Intelligence) - \u901A\u8FC7\u673A\u5668\u6A21\u62DF\u667A\u80FD\u884C\u4E3A</td></tr><tr><td>RPA</td><td>\u673A\u5668\u4EBA\u6D41\u7A0B\u81EA\u52A8\u5316 (Robotic Process Automation) - \u81EA\u52A8\u6267\u884C\u4EFB\u52A1</td></tr><tr><td>B2B</td><td>\u4F01\u4E1A\u5BF9\u4F01\u4E1A (Business to Business) - \u4F01\u4E1A\u95F4\u7684\u5546\u52A1\u4EA4\u6613</td></tr><tr><td>B2C</td><td>\u4F01\u4E1A\u5BF9\u6D88\u8D39\u8005 (Business to Customer) - \u4F01\u4E1A\u4E0E\u6D88\u8D39\u8005\u7684\u4EA4\u6613</td></tr><tr><td>e-commerce</td><td>\u7535\u5B50\u5546\u52A1 (Electronic Commerce) - \u4E92\u8054\u7F51\u4E0A\u7684\u5546\u4E1A\u4EA4\u6613</td></tr></tbody></table><h2 id="\u8F6F\u4EF6\u6D4B\u8BD5" tabindex="-1"><a class="header-anchor" href="#\u8F6F\u4EF6\u6D4B\u8BD5" aria-hidden="true">#</a> \u8F6F\u4EF6\u6D4B\u8BD5</h2><table><thead><tr><th>name</th><th>description</th></tr></thead><tbody><tr><td>ST</td><td>\u8F6F\u4EF6\u6D4B\u8BD5 (Software Testing)</td></tr><tr><td>LP</td><td>\u751F\u5B58\u9A8C\u8BC1\u6D4B\u8BD5 (Live Proving Test)\uFF0C\u5728\u751F\u4EA7\u73AF\u5883\u4E2D\u4F7F\u7528\u771F\u5B9E\u6570\u636E\u8FDB\u884C\u6D4B\u8BD5</td></tr><tr><td>SIT</td><td>\u7CFB\u7EDF\u96C6\u6210\u6D4B\u8BD5 (System Integration Testing)</td></tr><tr><td>UAT</td><td>\u7528\u6237\u9A8C\u6536\u6D4B\u8BD5 (User Acceptance Testing)</td></tr><tr><td>Unit</td><td>\u5355\u5143\u6D4B\u8BD5 (Unit Testing) - \u6D4B\u8BD5\u5355\u4E00\u7EC4\u4EF6\u6216\u4EE3\u7801\u5757</td></tr><tr><td>Integration</td><td>\u96C6\u6210\u6D4B\u8BD5 (Integration Testing) - \u6D4B\u8BD5\u591A\u4E2A\u7EC4\u4EF6\u6216\u7CFB\u7EDF\u7684\u7EC4\u5408</td></tr><tr><td>Performance</td><td>\u6027\u80FD\u6D4B\u8BD5 (Performance Testing) - \u6D4B\u8BD5\u8F6F\u4EF6\u5E94\u7528\u7684\u54CD\u5E94\u901F\u5EA6\u548C\u8D44\u6E90\u6D88\u8017\u7B49</td></tr><tr><td>Security</td><td>\u5B89\u5168\u6D4B\u8BD5 (Security Testing) - \u68C0\u67E5\u8F6F\u4EF6\u7684\u5B89\u5168\u6F0F\u6D1E</td></tr></tbody></table>',27);function a(i,s){return d}var o=t(r,[["render",a],["__file","glossary.html.vue"]]);export{o as default};
